INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('1', '1', 'Big Hero 6', 'The special bond that develops between plus-sized inflatable robot Baymax, and prodigy Hiro Hamada, who team up with a group of friends to form a band of high-tech heroes.', 'http://www.tudou.com/v/JzJGuAF7m94/&resourceId=0_04_05_99/v.swf', '01:42:00', 'hero6.jpg', '2015-02-11', '2015-05-29');
INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('2', '2', 'Avengers: Age of Ultron', 'When Tony Stark and Bruce Banner try to jump-start a dormant peacekeeping program called Ultron, things go horribly wrong and it\'s up to Earth\'s Mightiest Heroes to stop the villainous Ultron from enacting his terrible plans.', 'http://www.tudou.com/v/dHy2l0Y8xrI/&resourceId=0_04_05_99/v.swf', '02:21:00', 'avg.jpg', '2015-04-22', '2015-06-22');
INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('3', '3', 'Insidious: Chapter 3', 'A prequel set before the haunting of the Lambert family that reveals how gifted psychic Elise Rainier reluctantly agrees to use her ability to contact the dead in order to help a teenage girl who has been targeted by a dangerous supernatural entity.', 'http://www.tudou.com/v/u-gsoe7SX_I/&resourceId=0_04_05_99/v.swf', '01:37:00', 'insidious.jpg', '2015-07-08', '2015-09-08');
INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('4', '4', 'Jurassic World', 'Twenty-two years after the events of Jurassic Park, Isla Nublar now features a fully functioning dinosaur theme park, Jurassic World, as originally envisioned by John Hammond. After 10 years of operation and visitor rates declining, in order to fulfill a corporate mandate, a new attraction is created to re-spark visitor\'s interest, which backfires horribly.', 'http://www.tudou.com/v/lBaFz5AB1D0/&resourceId=0_04_05_99/v.swf', '02:10:00', 'jurassic.jpg', '2015-06-10', '2015-08-10');
INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('5', '5', 'Furious 7', 'Deckard Shaw seeks revenge against Dominic Toretto and his family for his comatose brother.', 'http://www.tudou.com/v/3x6VQRApcZU/&resourceId=0_04_05_99/v.swf', '02:17:00', 'furious7.jpg', '2015-04-01', '2015-06-30');
INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('6', '6', 'Paul Blart: Mall Cop 2', 'After six years of keeping our malls safe, Paul Blart has earned a well-deserved vacation. He heads to Vegas with his teenage daughter before she heads off to college. But safety never takes a holiday and when duty calls, Blart answers.', 'http://www.tudou.com/v/36rHCb2jRPI/&resourceId=0_04_05_99/v.swf', '01:34:00', 'mallcop.jpg', '2015-04-17', '2015-06-01');
INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('7', '7', 'San Andreas', 'In the aftermath of a massive earthquake in California, a rescue-chopper pilot makes a dangerous journey across the state in order to rescue his estranged daughter.', 'http://www.tudou.com/v/dvaA04WBu2w/&resourceId=0_04_05_99/v.swf', '01:54:00', 'andreas.jpg', '2015-05-29', '2015-07-29');
INSERT INTO `theater`.`movie` (`id`, `id_producer`, `title`, `synopsis`, `trailer`, `time`, `poster`, `release`, `end_release`) VALUES ('8', '8', 'Batkid Begins', 'On one day, in one city, the world comes together to grant one 5 year old cancer patient his wish. Batkid Begins looks at the why of this flash phenomenon.', 'http://www.tudou.com/v/hJTATCI9AvA/&resourceId=0_04_05_99/v.swf', '01:27:00', 'batkid.jpg', '2015-06-26', '2015-09-26');
